clear; clc; close all;t = 0:0.01:2;%%%%Controle Pkp=300;disp('Controlador'); numC=[kp]; denC=[1]; GC=tf(numC,denC);disp('Planta'); numP=[1]; denP=[1 10 20];disp('Ft ramo direito'); GP=tf(numP,denP);disp('FT malha fechada'); GL=GC*GP; G=feedback(GL,1);%exitação[C,T]=step(G,t); plot(T,C,'k')hold on%%%%Controle PIti=1; disp('Controlador'); numC=[kp*ti kp]; denC=[ti 0]; GC=tf(numC,denC);disp('Planta'); numP=[1]; denP=[1 10 20]; GP=tf(numP,denP)disp('FT ramo direito');GL=GC*GP;disp('FT malha fechada'); G=feedback(GL,1);%Exitação[C,T]=step(G,t); plot(T,C,'b')%% Controle PDhold ontd=0.05;disp('Controlador');numC=[kp*td kp]; denC=[1];GC=tf(numC,denC);disp('Planta');numP=[1]; denP=[1 10 20]; GP=tf(numP,denP)disp('FT ramo direito'); GL = GC*GP;disp('FT malha fechada'); G=feedback(GL,1);%exitação[C,T]=step(G,t); plot(T,C,'m');grid on%%%%Controle PIDti=1; td=0.05;disp('Controlador');numC=[kp*td*ti kp*ti kp]; denC=[ti 0];GC =tf(numC,denC); disp('Planta'); numP=[1]; denP=[1 10 20]; GP=tf(numP,denP);disp('FT ramo direito'); GL=GC*GP;disp('FT malha fechada'); G=feedback(GL,1);%exitação[C,T]=step(G,t); plot(T,C,'r','LineWidth',2)title('resposta ao degrau unitário, ações de controle','fontsize',14)ylabel('c(t)', 'fontsize', 14)xlabel('t(sec)','fontsize',14)legend('P','PI','PD','PID')