clear; clc; %AÇÃO DE CONTROLE PROPORCIONAL-DERIVATIVO(PD)t= 0:0.01:2;kp=300;td=0;num=[kp kp*td];den=[1 (kp*td+10) (20+kp)];G = tf(num,den)step(G,t);hold ontd=0.05;kp=300;num=[kp kp*td];den=[1 (kp*td+10) (20+kp)];G=tf(num,den)step(G,t);td=0.05;kp=600;num=[kp kp*td];den=[1 (kp*td+10) (20+kp)];G=tf(num,den)step(G,t);title('Resposta ao degrau unitário de G(s) = (kp+kdtds)/s^2+(10+kptd)s+(20+kp))','fontsize',14)ylabel('c(t)','fontsize',14)xlabel('t','fontsize',14);legend('Controle P: kp=300','Controle PD=kp=300, td=0.05','Controle PD:kp=600,td=0.05')