clear; clc; %AÇÃO DE CONTROLE PROPORCIONAL-INTEGRAL(PI)%Variando Kikp=1;ki = [20 50];sz = size(ki);for n=1:sz(2)    num = [kp ki(n)];    den = [1 10 (20+kp) ki(n)];    G = tf(num,den)    step(G);    hold onend%Variando Kphold onkp = 10;for n=1:sz(2)    num=[kp ki(n)];    den=[1 10 (20+kp) ki(n)];    G=tf(num,den)    step(G);    hold onendtitle('Resposta ao degrau unitário de G(s) = (Kps+ki)/s^3+10^2+(20+xp)s +ki)','fontsize',14)ylabel('c(t)','fontsize',14)xlabel('t','fontsize',14);legend('Kp=1 Ki=20','Kp=1 Ki=50','Kp=10 Ki=20','Kp=10 Ki=50')