clear; clc; close all;t = 0:0.01:6;num=[20]; den=[1 17 10];GL=tf(num,den);R=roots(den);numGH = [num/(R(1)*R(2))];denGH1 = [1/(-R(1)) 1]denGH2 = [1/(-R(2)) 1]erro_ss = 1/(1+numGH)G = feedback(GL,1)[Y,T]=step(G,t);plot(T,Y,T,1-Y)title('Resposta ao degrau unitário','fontsize',14)ylabel('c(t)','fontsize',14)xlabel('t (seg)','fontsize',14)legend('c(t)', 'e_s_s(t)')grid